package syntax.class09;

public class New {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
System.out.println("--------printing pattern using nested loops------------");
 

System.out.println("-------");
    
    for(int i=1;i<=4;i++) {
		for(int j=1;j<=5;j++) {
			System.out.print("*");
		}
		 System.out.println();
	}
    
    
    System.out.println("-------");
   
    
    //printing pattern 10x10
for(int i=0;i<10;i++) {
for(int j=0;j<10;j++) {
System.out.print("* ");
}  
 System.out.println();
}
    
    
}
}
